```js
const expect = require('chai').expect;      // use BDD assertions
const users = require('../bin/users');      // the library we're testing

describe('user', () => {

    beforeEach(() => {
        users.deleteAll();
    });

    it('has no users', () => {
        const actual = users.getAll().length;
        expect(actual).to.equal(0);
    });

